mapper.select:
select * from ( 
	select rownum rnum, B.* from (
		select * from notice order by notice_idx desc
	) B
	order by notice_idx desc
) R
where R.rnum between #{startNo} AND #{endNo}
order by rnum

select * from 
	(select notice_idx, 
		lag(notice_idx,1) over(order by notice_idx) prev, 
		lag(notice_title,1) over(order by notice_idx) prev_title 
	from notice)
where notice_idx = #{notice_idx}

select * from 
	(select notice_idx, 
		lead(notice_idx,1) over(order by notice_idx) next, 
		lead(notice_title,1) over(order by notice_idx) next_title 
	from notice)
where notice_idx = #{notice_idx}

select count(*) cut from(
	select * from notice
	
) cnt

mapper.update:
UPDATE notice set 
			 n_type_idx = #{NTypeIdx},
			 notice_title = #{noticeTitle}, 
			 notice_content = #{noticeContent} 
where notice_idx = #{noticeIdx}

UPDATE inquiry 
SET reply_status = 1
WHERE inq_idx = #{inqIdx }

mapper.select:
	 	select * from ( 
