  		order by faq_idx desc
  	) R
  	where R.rnum between #{startNo} AND #{endNo}
  	order by rnum

	select count(*) cut from(
  		select * from faq  		
  	) cnt

mapper.update:
UPDATE faq set
        f_type_idx = #{FTypeIdx},
        faq_question = #{faqQuestion},
        faq_answer = #{faqAnswer} 
    where faq_idx = #{faqIdx}
