mapper.select:
SELECT COUNT(*) FROM book
WHERE pfm_idx = #{pfm.pfmIdx }
	AND book_cate_idx = 2
	AND trunc(create_date,'dd') BETWEEN #{startDate } AND #{endDate }

SELECT count(*) FROM ( SELECT * FROM artist
WHERE name LIKE '%${value }%'
)

SELECT hf.origin_name as originName , hf.stored_name as storedName FROM ( SELECT * FROM hall
WHERE hall_name LIKE '%${value }%'
) h LEFT JOIN hall_file hf ON h.hall_idx = hf.hall_idx

SELECT count(*) FROM ( SELECT * FROM hall
WHERE hall_name LIKE '%${value }%'
)

SELECT h.hall_name as hallName , h.hall_phone as hallPhone FROM ( SELECT * FROM hall
WHERE hall_name LIKE '%${value }%'
) h LEFT JOIN hall_file hf ON h.hall_idx = hf.hall_idx

SELECT po.stored_name poster_name , po.origin_name originName , pp.* FROM ( SELECT ROWNUM as rnum, p.* FROM ( SELECT * FROM performance
WHERE NAME LIKE '%${value }%' AND genre_idx=#{genreIdx}
ORDER BY pfm_start desc ) p )pp LEFT JOIN poster_info po ON pp.pfm_idx = po.pfm_idx ORDER BY rnum

SELECT COUNT(*) FROM PFM_DATEBYTIME
WHERE pfm_idx = #{pfm.pfmIdx }
	AND pfm_date BETWEEN #{startDate } AND #{endDate }
