mapper.select:
SELECT count(*)
FROM performance
WHERE genre_idx = #{genreIdx }

SELECT * FROM ( SELECT ROWNUM as rnum, A.* FROM ( SELECT * FROM ( SELECT DISTINCT P.pfm_idx , (SELECT stored_name FROM poster_info PI WHERE PI.pfm_idx = P.pfm_idx) poster_name , theme_idx FROM performance P INNER JOIN pfm_theme PT ON P.pfm_idx = PT.pfm_idx
WHERE theme_idx IN
#{theme.themeIdx}
) WHERE PFM_IDX NOT IN ( SELECT pfm_idx FROM my_choice WHERE user_idx= 1 ) ) A )
WHERE rnum <11

mapper.update:
UPDATE performance
SET 
	name=#{name }
	, genre_idx=#{genreIdx }
	, pfm_start=#{pfmStart }
	, pfm_end=#{pfmEnd }
	, ticket_start=#{ticketStart }
	, ticket_end=#{ticketEnd }
	, hall_idx=#{hallIdx }
	, age_grade_idx=#{ageGradeIdx }
	, running_time=#{runningTime }
	, create_date=SYSDATE

mapper.select:
SELECT COUNT(*) FROM book
WHERE pfm_idx = #{pfm.pfmIdx }
	AND book_cate_idx = 2
	AND trunc(create_date,'dd') BETWEEN #{startDate } AND #{endDate }

SELECT * FROM ( SELECT ROWNUM no, PP.* FROM (SELECT p.*, pi.stored_name, h.hall_name FROM performance p JOIN poster_info pi ON p.pfm_idx = pi.pfm_idx JOIN hall h ON p.hall_idx = h.hall_idx
WHERE p.name LIKE '%' || #{search} || '%'
ORDER BY p.create_date DESC) PP ) WHERE no BETWEEN #{startNo} AND #{endNo}

SELECT * FROM ( SELECT ROWNUM no, PP.* FROM (SELECT p.*, pi.stored_name, h.hall_name FROM performance p JOIN poster_info pi ON p.pfm_idx = pi.pfm_idx JOIN hall h ON p.hall_idx = h.hall_idx
AND p.name LIKE '%' || #{search} || '%'
ORDER BY p.create_date DESC) PP ) WHERE no BETWEEN #{startNo} AND #{endNo}

SELECT * FROM ( SELECT ROWNUM no, PP.* FROM (SELECT p.*, pi.stored_name, h.hall_name FROM performance p JOIN poster_info pi ON p.pfm_idx = pi.pfm_idx JOIN hall h ON p.hall_idx = h.hall_idx WHERE p.genre_idx = 1
AND p.name LIKE '%' || #{search} || '%'
ORDER BY p.create_date DESC) PP ) WHERE no BETWEEN #{startNo} AND #{endNo}

SELECT * FROM ( SELECT ROWNUM no, PP.* FROM (SELECT p.*, pi.stored_name, h.hall_name FROM performance p JOIN poster_info pi ON p.pfm_idx = pi.pfm_idx JOIN hall h ON p.hall_idx = h.hall_idx WHERE p.genre_idx = 2
AND p.name LIKE '%' || #{search} || '%'
ORDER BY p.create_date DESC) PP ) WHERE no BETWEEN #{startNo} AND #{endNo}

SELECT COUNT(*) FROM PFM_DATEBYTIME
WHERE pfm_idx = #{pfm.pfmIdx }
	AND pfm_date BETWEEN #{startDate } AND #{endDate }

SELECT * FROM ( SELECT ROWNUM no, PP.* FROM (SELECT p.*, pi.stored_name, h.hall_name FROM performance p JOIN poster_info pi ON p.pfm_idx = pi.pfm_idx JOIN hall h ON p.hall_idx = h.hall_idx
AND p.name LIKE '%' || #{search} || '%'
ORDER BY p.name) PP ) WHERE no BETWEEN #{startNo} AND #{endNo}

SELECT * FROM ( SELECT ROWNUM no, PP.* FROM (SELECT p.*, pi.stored_name, h.hall_name FROM performance p JOIN poster_info pi ON p.pfm_idx = pi.pfm_idx JOIN hall h ON p.hall_idx = h.hall_idx WHERE p.genre_idx = 1
AND p.name LIKE '%' || #{search} || '%'
ORDER BY p.name) PP ) WHERE no BETWEEN #{startNo} AND #{endNo}

SELECT * FROM ( SELECT ROWNUM no, PP.* FROM (SELECT p.*, pi.stored_name, h.hall_name FROM performance p JOIN poster_info pi ON p.pfm_idx = pi.pfm_idx JOIN hall h ON p.hall_idx = h.hall_idx WHERE p.genre_idx = 2
AND p.name LIKE '%' || #{search} || '%'
ORDER BY p.name) PP ) WHERE no BETWEEN #{startNo} AND #{endNo}

mapper.update:
UPDATE main_banner 
SET banner_img_ori = #{bannerImgOri}, banner_img_str = #{bannerImgStr}, thumb_img_ori = #{thumbImgOri}, thumb_img_str = #{thumbImgStr}
WHERE pfm_idx = #{pfmIdx}
