mapper.select:
SELECT user_idx FROM userinfo
 where 1=1

 	AND email = #{email}


 	AND password = #{password}

SELECT * FROM ( SELECT ROWNUM NO, B.* FROM( SELECT u.user_idx as userIdx, u.email, u.nick, sum(p.paid_amount) amount, count(p.paid_amount) totalCnt, u.create_date as createDate, u.m_grade_idx as mGradeIdx FROM userinfo u JOIN payment p ON u.user_idx = p.user_idx(+)
WHERE email LIKE '%' || #{search} || '%' OR nick LIKE '%' || #{search} || '%'
GROUP BY u.user_idx, u.email, u.nick, u.create_date, u.m_grade_idx ORDER BY u.create_date) B ) WHERE no between #{startNo} AND #{endNo}

mapper.update:
UPDATE userinfo
SET m_grade_idx = #{currUserGrade}
WHERE user_idx = #{currUser}

mapper.select:
SELECT * FROM ( SELECT ROWNUM AS no, test2.* FROM ( SELECT test.*, COUNT(state) AS ea FROM ( SELECT u.user_idx, trunc(b.create_date,'dd') create_date, u.email, u.nick, p.name, p.pfm_start, p.pfm_end, b.book_group, trunc(b.cancel_date,'dd') cancel_date, bc.state,pi.stored_name, h.hall_name, pd.pfm_date, pd.pfm_time FROM book b JOIN performance p ON b.pfm_idx = p.pfm_idx JOIN poster_info pi ON p.pfm_idx = pi.pfm_idx JOIN hall h ON p.hall_idx = h.hall_idx JOIN pfm_datebytime pd ON b.pfm_dbt_idx = pd.pfm_dbt_idx JOIN book_cate bc ON b.book_cate_idx = bc.book_cate_idx JOIN userinfo u ON b.user_idx = u.user_idx
WHERE p.name LIKE '%' || #{search} || '%' OR u.nick LIKE '%' || #{search} || '%'
) test GROUP BY create_date, email, nick, user_idx, name, pfm_start, pfm_end, book_group, cancel_date, state, stored_name, hall_name, pfm_date, pfm_time ORDER by user_idx DESC ) test2 ) WHERE no between #{startNo} AND #{endNo}


SELECT password 
FROM userinfo 
WHERE user_idx= #{userIdx }

mapper.update:
 	UPDATE  userinfo 
	SET nick = #{nick }
	WHERE user_idx= #{userIdx }

 	UPDATE  userinfo 
	SET password = #{password }
	WHERE email= #{email }

 	UPDATE  userinfo 
	SET phone = #{phone }
	WHERE user_idx= #{userIdx }

 	UPDATE  userinfo 
	SET 
		postcode = #{postcode }
		,addr = #{addr }


		,addr_detail = #{addrDetail }

	WHERE user_idx= #{userIdx }

UPDATE userinfo 
SET profile =#{profile }
WHERE user_idx = #{userIdx }

mapper.select:
SELECT COUNT(*) FROM (SELECT test.*, COUNT(state) AS ea FROM ( SELECT u.user_idx, trunc(b.create_date,'dd') create_date, u.email, u.nick, p.name, p.pfm_start, p.pfm_end, b.book_group, trunc(b.cancel_date,'dd') cancel_date, bc.state,pi.stored_name, h.hall_name, pd.pfm_date, pd.pfm_time FROM book b JOIN performance p ON b.pfm_idx = p.pfm_idx JOIN poster_info pi ON p.pfm_idx = pi.pfm_idx JOIN hall h ON p.hall_idx = h.hall_idx JOIN pfm_datebytime pd ON b.pfm_dbt_idx = pd.pfm_dbt_idx JOIN book_cate bc ON b.book_cate_idx = bc.book_cate_idx JOIN userinfo u ON b.user_idx = u.user_idx
WHERE p.name LIKE '%' || #{value} || '%' OR u.nick LIKE '%' || #{value} || '%'
) test GROUP BY create_date, email, nick, user_idx, name, pfm_start, pfm_end, book_group, cancel_date, state, stored_name, hall_name, pfm_date, pfm_time ORDER by user_idx DESC)
